import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "../styles/Accepted.css"; // Importing the CSS file

const Pending = () => {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/projects')
            .then(response => {
                const pendingProjects = response.data.filter(project => project.status === 'pending');
                setProjects(pendingProjects);
            });
    }, []);

    return (
        <div className="container">
            <h1 className="heading-style">Pending Projects</h1>
            <ul className="project-list">
                {projects.map(project => (
                    <li key={project._id}>
                        <span className="project-name">{project.name}</span>
                        <button className="project-status">{project.status}</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Pending;
