import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/projects')
            .then(response => {
                setProjects(response.data);
            });
    }, []);

    const categorizedProjects = {
        accepted: [],
        'in-progress': [],
        pending: [],
        done: []
    };

    projects.forEach(project => {
        if (project.status === 'accepted') categorizedProjects.accepted.push(project);
        if (project.status === 'in-progress') categorizedProjects['in-progress'].push(project);
        if (project.status === 'pending') categorizedProjects.pending.push(project);
        if (project.status === 'done') categorizedProjects.done.push(project);
    });

    return (
        <div>
            <h1>All Projects</h1>
            <h2>Accepted Projects</h2>
            <ul>
                {categorizedProjects.accepted.map(project => (
                    <li key={project._id}>{project.name}</li>
                ))}
            </ul>
            <h2>In Progress Projects</h2>
            <ul>
                {categorizedProjects['in-progress'].map(project => (
                    <li key={project._id}>{project.name}</li>
                ))}
            </ul>
            {/* <h2>Pending Projects</h2> */}
            <ul>
                {categorizedProjects.pending.map(project => (
                    <li key={project._id}>{project.name}</li>
                ))}
            </ul>
            {/* <h2>Completed Projects</h2> */}
            <ul>
                {categorizedProjects.done.map(project => (
                    <li key={project._id}>{project.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default Home;
