import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Dropdown, DropdownButton } from 'react-bootstrap';

const Home = () => {
    const [projects, setProjects] = useState([]);
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = () => {
        axios.get('http://localhost:5000/api/projects')
            .then(response => {
                setProjects(response.data);
            });
    };

    const handleSelectFilter = (eventKey) => {
        setFilter(eventKey);
    };

    const handleUpdateStatus = (id, status) => {
        axios.put(`http://localhost:5000/api/projects/${id}`, { status })
            .then(() => {
                fetchProjects();
            });
    };

    const filteredProjects = projects.filter(project => {
        if (filter === 'all') return true;
        return project.status === filter;
    });

    return (
        <div className="container">
            <h1>All Projects</h1>
            <div className="project-list">
                <ul>
                    {filteredProjects.map(project => (
                        <li key={project._id} className="project-item">
                            <span>{project.name}</span>
                            <DropdownButton
                                id={`dropdown-${project._id}`}
                                title="Update Status"
                                className="status-dropdown"
                                onSelect={(eventKey) => handleUpdateStatus(project._id, eventKey)}
                            >
                                <Dropdown.Item eventKey="accepted">Accepted</Dropdown.Item>
                                <Dropdown.Item eventKey="in-progress">In Progress</Dropdown.Item>
                                <Dropdown.Item eventKey="pending">Pending</Dropdown.Item>
                                <Dropdown.Item eventKey="done">Completed</Dropdown.Item>
                            </DropdownButton>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
